def list_count(nums):
    count_0 = 0
    count_1 = 0
    count_2 = 0

    for num in nums:
        if num == 0:
            count_0 += 1
        elif num == 1:
            count_1 += 1
        elif num == 2:
            count_2 += 1


    return count_0, count_1, count_2

